import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import * as THREE from 'three';
import { OBJLoader } from 'three/examples/jsm/loaders/OBJLoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

function AvatarCreator() {
    const [file, setFile] = useState(null);
    const [status, setStatus] = useState('Idle');
    const [avatarId, setAvatarId] = useState('');
    const [exportUrl, setExportUrl] = useState('');
    const apiToken = 'eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJuSnVpYzVwbXk1T1hGSjVmY1RIQTdUNVktRHZVbVVOR2xxVHBqS0hDVnU4In0..iD651MrA49XCFar_hRrr3Wea9SOJKQ9S_voJ3sRAPt5zMMzyvI-tSVP6mBD4UPD4Rzmi4QOqzNz5KamJX-0kXfyF7VM6jSERPfyDwhBY8SeKJTihcyIkOzcPn--ymrtnJymZYgp9bGqduaRab9-z_Y9kwsrdyEeAzta8EOAFusP91dK1VHZPmoJYk9tBP3ckZxIAI_fpZWopgvZR5jCnRbj1YuuVD5yyFZGS4f3wGstrh_bt6KcMwNpbWIfGmMBDWaoOYIgsh2PW0UtnrUvJqINQ4FEVGQR_QM-Yh8m5ls38pQMB3DiF78cFqdSbII4WNzk9w1jU8E3-lLf2XZIsUw'; // Ensure to replace this with your actual API token
    const viewerRef = useRef(null);

    // Load and display the model once export URL is set
    useEffect(() => {
        if (exportUrl) {
            loadOBJModel(exportUrl);
        }
    }, [exportUrl]);

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (!file) {
            alert('Please select a file.');
            return;
        }

        setStatus('Creating Avatar...');
        try {
            const avatarId = await createAvatar();
            await uploadImage(avatarId);
            await pollFittingStatus(avatarId);
            await initiateExport(avatarId);
        } catch (error) {
            console.error('An error occurred:', error);
            setStatus('Error in process');
        }
    };

    async function createAvatar() {
        const response = await axios.post('https://api.meshcapade.com/api/v1/avatars/create/from-images', {}, {
            headers: { Authorization: `Bearer ${apiToken}` },
        });
        const avatarId = response.data.data.id;
        setAvatarId(avatarId);
        return avatarId;
    }

    async function uploadImage(avatarId) {
        const uploadLinkResponse = await axios.post(`https://api.meshcapade.com/api/v1/avatars/${avatarId}/images`, {}, {
            headers: { Authorization: `Bearer ${apiToken}` },
        });
        const uploadUrl = uploadLinkResponse.data.data.links.upload;
        await axios.put(uploadUrl, file, {
            headers: { 'Content-Type': 'image/png' },
        });
        setStatus('Image uploaded. Processing...');
    }

    async function pollFittingStatus(avatarId) {
        let isReady = false;
        while (!isReady) {
            const statusResponse = await axios.get(`https://api.meshcapade.com/api/v1/avatars/${avatarId}`, {
                headers: { Authorization: `Bearer ${apiToken}` },
            });
            if (statusResponse.data.data.attributes.state === 'READY') {
                isReady = true;
                setStatus('Fitting process complete. Initiating export...');
            } else {
                await new Promise(resolve => setTimeout(resolve, 5000)); // Poll every 5 seconds
            }
        }
    }

    async function initiateExport(avatarId) {
        const exportResponse = await axios.post(`https://api.meshcapade.com/api/v1/avatars/${avatarId}/export`, {
            format: 'obj', // or 'fbx' depending on your needs
            pose: 't',
        }, {
            headers: { Authorization: `Bearer ${apiToken}`, 'Content-Type': 'application/json' },
        });
        pollExportStatus(exportResponse.data.data.id);
    }

    async function pollExportStatus(meshId) {
        let isExported = false;
        while (!isExported) {
            const exportStatusResponse = await axios.get(`https://api.meshcapade.com/api/v1/meshes/${meshId}`, {
                headers: { Authorization: `Bearer ${apiToken}` },
            });
            if (exportStatusResponse.data.data.attributes.state === 'READY') {
                isExported = true;
                setExportUrl(exportStatusResponse.data.data.attributes.url.path);
                setStatus('Export complete. Model ready for viewing.');
            } else {
                await new Promise(resolve => setTimeout(resolve, 5000)); // Poll every 5 seconds
            }
        }
    }

    function loadOBJModel(url) {
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, viewerRef.current.clientWidth / viewerRef.current.clientHeight, 0.1, 1000);
        camera.position.z = 5;

        const renderer = new THREE.WebGLRenderer();
        renderer.setSize(viewerRef.current.clientWidth, viewerRef.current.clientHeight);
        viewerRef.current.appendChild(renderer.domElement);

        new OrbitControls(camera, renderer.domElement);

        new OBJLoader().load(url, (obj) => {
            scene.add(obj);
            animate();
        });

        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }
    }

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input type="file" onChange={handleFileChange} />
                <button type="submit">Create Avatar</button>
            </form>
            <div>Status: {status}</div>
            <div ref={viewerRef} style={{ width: '600px', height: '400px', background: '#aaa' }}></div>
        </div>
    );
}

export default AvatarCreator;
