import React, { useState } from 'react';
import axios from 'axios';
import { Canvas, useLoader } from '@react-three/fiber';
import { OBJLoader } from 'three/examples/jsm/loaders/OBJLoader';

function App() {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('Idle');
  const [assetID, setAssetID] = useState('');
  const [modelUrl, setModelUrl] = useState('');
  const apiToken = 'eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJuSnVpYzVwbXk1T1hGSjVmY1RIQTdUNVktRHZVbVVOR2xxVHBqS0hDVnU4In0..kBvnHgNaV-NAQgpUS7N97A-Q0UoPe7yu6tq-QbO4LVTGeI7Urkp2NllBEmV950EzJBHtz5Oe9gVd6LLitOciP1n_YmEbiuRT7uVhbIU8bGL5PKinpcAH1kMwDsc6yxDm8lVWN1Wx8OE_v8ydgR29tzGfKBPDOCavBW0xiLuMKb1DX0BLsQWmrkSpqANjkufnXq5gPRW36lWSytEw_1new8NAEOYrPiZK9yBv-waqiicNA32pPL11-HwyNtsGtdK5rQUkHnBlACW4ScROW6Hz6HhaN3rFEwAnCXUHuMpxI_1yh_cDiBWKenAoA0pgwnBaNszbU2jwVihTWGW20g2v2w';

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const createAvatar = async () => {
    setStatus('Creating Avatar...');
    try {
      const response = await axios.post('https://api.meshcapade.com/api/v1/avatars/create/from-images', {}, {
        headers: { 'Authorization': `Bearer ${apiToken}` },
      });
      const newAssetID = response.data.id; // Update according to actual API response structure
      setAssetID(newAssetID);
      setStatus('Avatar Created');
      return newAssetID;
    } catch (error) {
      console.error('Error creating avatar:', error);
      setStatus('Error Creating Avatar');
    }
  };

  const uploadImage = async (assetID) => {
    setStatus('Uploading Image...');
    const formData = new FormData();
    formData.append('file', file);

    try {
      await axios.post(`https://api.meshcapade.com/api/v1/avatars/${assetID}/images/upload`, formData, {
        headers: {
          'Authorization': `Bearer ${apiToken}`,
          'Content-Type': 'multipart/form-data',
        },
      });
      setStatus('Image Uploaded');
    } catch (error) {
      console.error('Error uploading image:', error);
      setStatus('Error Uploading Image');
    }
  };

  const startFittingProcess = async (assetID) => {
    setStatus('Starting Fitting Process...');
    try {
      await axios.post(`https://api.meshcapade.com/api/v1/avatars/${assetID}/fit-to-images`, {}, {
        headers: { 'Authorization': `Bearer ${apiToken}` },
      });
      setStatus('Fitting Process Started');
    } catch (error) {
      console.error('Error starting fitting process:', error);
      setStatus('Error Starting Fitting Process');
    }
  };
  const exportAvatarMesh = async (assetID) => {
    setStatus('Exporting Avatar Mesh...');
    try {
      // Replace this URL with the actual endpoint for exporting the avatar mesh
      const exportResponse = await axios.post(`https://api.meshcapade.com/api/v1/avatars/${assetID}/export`, {}, {
        headers: { 'Authorization': `Bearer ${apiToken}` },
      });
      // Assuming the response includes a URL to the exported mesh
      setModelUrl(exportResponse.data.downloadUrl); // Adjust based on actual API response
      setStatus('Mesh Exported');
    } catch (error) {
      console.error('Error exporting avatar mesh:', error);
      setStatus('Error Exporting Mesh');
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!file) {
      alert('Please select a file.');
      return;
    }

    const createdAssetID = await createAvatar();
    if (createdAssetID) {
      await uploadImage(createdAssetID);
      await startFittingProcess(createdAssetID);
      await exportAvatarMesh(createdAssetID); // Add this line to export the mesh
    }
  };

  const Model = () => {
    const obj = useLoader(OBJLoader, modelUrl);
    return <primitive object={obj} />;
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} />
        <button type="submit">Upload and Create Avatar</button>
      </form>
      <div>Status: {status}</div>
      {modelUrl && (
        <Canvas>
          <ambientLight intensity={0.5} />
          <Model />
        </Canvas>
      )}
    </div>
  );
}

export default App;
