import React, { useRef, useEffect } from 'react';
import * as THREE from 'three';
import { OBJLoader } from 'three/examples/jsm/loaders/OBJLoader';

const ObjViewer = () => {
  const mountRef = useRef(null);
  // Declare both scene and camera here to be initialized in useEffect and used globally within the component.
  let scene;
  let camera;

  useEffect(() => {
    // Initialize scene, camera, and renderer
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    mountRef.current.appendChild(renderer.domElement);

    scene.background = new THREE.Color(0xdddddd);

    // Basic lighting setup
    const ambientLight = new THREE.AmbientLight(0x404040); // soft white light
    scene.add(ambientLight);
    const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
    directionalLight.position.set(0, 1, 1);
    scene.add(directionalLight);

    // Camera setup
    camera.position.z = 5;

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);
      renderer.render(scene, camera);
    };
    animate();

    // Adjust renderer and camera aspect ratio on resize
    const handleResize = () => {
      renderer.setSize(window.innerWidth, window.innerHeight);
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
    };
    window.addEventListener('resize', handleResize);

    // Cleanup on component unmount
    return () => {
      window.removeEventListener('resize', handleResize);
      mountRef.current.removeChild(renderer.domElement);
    };
  }, []);

  const handleUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const objData = e.target.result;
        const loader = new OBJLoader();
        loader.load(objData, (obj) => {
          // Adjust the object before adding it to the scene
          obj.scale.set(0.1, 0.1, 0.1);
          scene.add(obj);
          // Center the camera on the loaded object
          const box = new THREE.Box3().setFromObject(obj);
          const center = box.getCenter(new THREE.Vector3());
          camera.position.z = center.z + 5; // Adjust camera position based on the object's size
        }, undefined, (error) => {
          console.error('An error happened during the loading of the OBJ file:', error);
        });
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <>
      <input type="file" onChange={handleUpload} accept=".obj" />
      <div ref={mountRef} style={{ width: '100vw', height: '100vh' }}></div>
    </>
  );
};

export default ObjViewer;
