import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Canvas } from '@react-three/fiber'; // Assuming you're using react-three-fiber for 3D model rendering
import { OBJModel } from 'react-three-obj-loader'; // This is hypothetical; adapt based on your chosen loader and format

function App() {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('Idle');
  const [assetID, setAssetID] = useState('');
  const [modelUrl, setModelUrl] = useState('');
  const apiToken = 'eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJuSnVpYzVwbXk1T1hGSjVmY1RIQTdUNVktRHZVbVVOR2xxVHBqS0hDVnU4In0..kBvnHgNaV-NAQgpUS7N97A-Q0UoPe7yu6tq-QbO4LVTGeI7Urkp2NllBEmV950EzJBHtz5Oe9gVd6LLitOciP1n_YmEbiuRT7uVhbIU8bGL5PKinpcAH1kMwDsc6yxDm8lVWN1Wx8OE_v8ydgR29tzGfKBPDOCavBW0xiLuMKb1DX0BLsQWmrkSpqANjkufnXq5gPRW36lWSytEw_1new8NAEOYrPiZK9yBv-waqiicNA32pPL11-HwyNtsGtdK5rQUkHnBlACW4ScROW6Hz6HhaN3rFEwAnCXUHuMpxI_1yh_cDiBWKenAoA0pgwnBaNszbU2jwVihTWGW20g2v2w';

  // Replace 'your_api_token_here' with the actual API token

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  // Assuming createAvatar, uploadImage, and startFittingProcess are defined here

  const exportAvatarMesh = async (assetID) => {
    try {
      const response = await axios.post(`https://api.meshcapade.com/api/v1/avatars/${assetID}/export`, {}, {
        headers: { 'Authorization': `Bearer ${apiToken}` },
      });
      const exportJobID = response.data.exportJobID; // Adjust according to actual response
      checkExportStatus(exportJobID);
    } catch (error) {
      console.error('Error exporting avatar mesh:', error);
      setStatus('Error Exporting Avatar Mesh');
    }
  };

  const checkExportStatus = async (exportJobID) => {
    try {
      // Implement a polling mechanism here
      // This example uses a simple timeout for illustration; adjust according to your needs
      const intervalId = setInterval(async () => {
        const statusResponse = await axios.get(`https://api.meshcapade.com/api/v1/exports/${exportJobID}/status`, {
          headers: { 'Authorization': `Bearer ${apiToken}` },
        });
        if (statusResponse.data.status === 'READY') {
          clearInterval(intervalId);
          setModelUrl(statusResponse.data.downloadUrl); // Assuming the API returns a direct download URL
          setStatus('Export Ready');
        }
      }, 5000); // Check every 5 seconds; adjust as needed
    } catch (error) {
      console.error('Error checking export status:', error);
      setStatus('Error Checking Export Status');
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!file) {
      alert('Please select a file.');
      return;
    }

    const createdAssetID = await createAvatar();
    if (createdAssetID) {
      await uploadImage(createdAssetID);
      await startFittingProcess(createdAssetID);
      await exportAvatarMesh(createdAssetID); // New step to export the mesh
      // The model URL will be set once the export is ready and checked by checkExportStatus
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} />
        <button type="submit">Upload and Create Avatar</button>
      </form>
      <div>Status: {status}</div>
      {modelUrl && (
        <Canvas>
          <ambientLight />
          <pointLight position={[10, 10, 10]} />
          <OBJModel url={modelUrl} /> {/* Adapt based on your 3D model and chosen loader */}
        </Canvas>
      )}
    </div>
  );
}

export default App;
