import React, { useState } from 'react';
import axios from 'axios';

function AvatarCreator() {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('Idle');
  const [avatarId, setAvatarId] = useState('');
  const apiToken = 'eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJuSnVpYzVwbXk1T1hGSjVmY1RIQTdUNVktRHZVbVVOR2xxVHBqS0hDVnU4In0..Ver6BtxgNZ-jY91KSD-iNVQD5Mze93NOOjwK1NqlR18vmvNbbnYLMd__j358SRpIpeyZ_ZtyXL5IwP_okYdY31LxNvR7B80sf96Vg3QeA4f1t3QbKrYY8O2tFXCOIgSIBsak5efWqRKHAQdc1dZwvltJ0g7fGAEkOMwL6FLYhoHzQSzQYroohbfgXpciH8HQmYcT5HA6hWbJUPeeBQW7jlGEKwrI9FY9WbpP9TBxWpQYAASElukRIyxblcC0AvsK6eXtdHE2oyjBgKvOAaPGccEFrU_4ZByuaccSbPyHZJliRqfz88ob4mvDZqEmgcHRYuOHBUZjM0YzdMxJtXa7sg'; // Replace with your actual token

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const createAvatarAndGetUploadLink = async () => {
    setStatus('Creating Avatar...');
    try {
      const response = await axios.post('https://api.meshcapade.com/api/v1/avatars/create/from-images', {}, {
        headers: { 'Authorization': `Bearer ${apiToken}` },
      });
      const avatarId = response.data.data.id;
      setAvatarId(avatarId);
      setStatus('Avatar Created. Obtaining Upload Link...');

      const uploadLinkResponse = await axios.post(`https://api.meshcapade.com/api/v1/avatars/${avatarId}/images`, {}, {
        headers: { 'Authorization': `Bearer ${apiToken}` },
      });
      return uploadLinkResponse.data.data.links.upload; 
    } catch (error) {
      console.error('Error in avatar creation or obtaining upload link:', error);
      setStatus('Error in Process');
      throw error;
    }
  };

  const uploadImage = async (uploadUrl) => {
    try {
      await axios.put(uploadUrl, file, {
        headers: { 'Content-Type': 'image/png' },
      });
      setStatus('Image Uploaded. Starting Fitting Process...');
      startFittingProcess();
    } catch (error) {
      console.error('Error uploading image:', error);
      setStatus('Error Uploading Image');
      throw error;
    }
  };

  const startFittingProcess = async () => {
    try {
      await axios.post(`https://api.meshcapade.com/api/v1/avatars/${avatarId}/fit-to-images`, {
        avatarname: "test",
        gender: "FEMALE",
        height: 185,
        imageMode: "AFI",
        weight: 85
      }, {
        headers: { 'Authorization': `Bearer ${apiToken}`, 'Content-Type': 'application/json' },
      });
      setStatus('Fitting Process Started. Please wait...');
      // Implement polling or another mechanism here to check the avatar's status
    } catch (error) {
      console.error('Error starting fitting process:', error);
      setStatus('Error Starting Fitting Process');
      throw error;
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!file) {
      alert('Please select a file.');
      return;
    }

    try {
      const uploadUrl = await createAvatarAndGetUploadLink();
      await uploadImage(uploadUrl);
    } catch (error) {
      // Handle errors appropriately
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} />
        <button type="submit">Create Avatar</button>
      </form>
      <div>Status: {status}</div>
      {avatarId && <div>Avatar ID: {avatarId}</div>}
    </div>
  );
}

export default AvatarCreator;
