import React, { useState, useRef, Suspense } from 'react';
import axios from 'axios';
import { Canvas } from '@react-three/fiber';
import { OBJLoader } from 'three/examples/jsm/loaders/OBJLoader';
import { useLoader } from '@react-three/fiber';

// Component to load and display OBJ file
function ObjModel({ objUrl }) {
  const obj = useLoader(OBJLoader, objUrl);
  return <primitive object={obj} scale={0.5} />;
}

function App() {
  const [file, setFile] = useState(null);
  const [objUrl, setObjUrl] = useState('');
  const apiToken = 'eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJuSnVpYzVwbXk1T1hGSjVmY1RIQTdUNVktRHZVbVVOR2xxVHBqS0hDVnU4In0..kBvnHgNaV-NAQgpUS7N97A-Q0UoPe7yu6tq-QbO4LVTGeI7Urkp2NllBEmV950EzJBHtz5Oe9gVd6LLitOciP1n_YmEbiuRT7uVhbIU8bGL5PKinpcAH1kMwDsc6yxDm8lVWN1Wx8OE_v8ydgR29tzGfKBPDOCavBW0xiLuMKb1DX0BLsQWmrkSpqANjkufnXq5gPRW36lWSytEw_1new8NAEOYrPiZK9yBv-waqiicNA32pPL11-HwyNtsGtdK5rQUkHnBlACW4ScROW6Hz6HhaN3rFEwAnCXUHuMpxI_1yh_cDiBWKenAoA0pgwnBaNszbU2jwVihTWGW20g2v2w';

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append('file', file);
    const config = {
      headers: {
        'Content-Type': 'multipart/form-data',
        'Authorization': `Bearer ${apiToken}`
      }
    };

    try {
      const response = await axios.post('/api/v1/avatars/create/from-images', formData, config);
      // Assuming the response contains a direct URL to the OBJ file
      setObjUrl(response.data.objFileUrl);
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} />
        <button type="submit">Upload</button>
      </form>
      {objUrl && (
        <Suspense fallback={<div>Loading...</div>}>
          <Canvas>
            <ambientLight intensity={0.5} />
            <directionalLight position={[0, 0, 5]} />
            <ObjModel objUrl={objUrl} />
          </Canvas>
        </Suspense>
      )}
    </div>
  );
}

export default App;
