import React, { useState } from 'react';
import axios from 'axios';
import { Canvas } from '@react-three/fiber';
import { useLoader } from '@react-three/fiber';
import { OBJLoader } from 'three/examples/jsm/loaders/OBJLoader';

function App() {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('Idle');
  const [modelUrl, setModelUrl] = useState('');
  const apiToken = 'eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJuSnVpYzVwbXk1T1hGSjVmY1RIQTdUNVktRHZVbVVOR2xxVHBqS0hDVnU4In0..kBvnHgNaV-NAQgpUS7N97A-Q0UoPe7yu6tq-QbO4LVTGeI7Urkp2NllBEmV950EzJBHtz5Oe9gVd6LLitOciP1n_YmEbiuRT7uVhbIU8bGL5PKinpcAH1kMwDsc6yxDm8lVWN1Wx8OE_v8ydgR29tzGfKBPDOCavBW0xiLuMKb1DX0BLsQWmrkSpqANjkufnXq5gPRW36lWSytEw_1new8NAEOYrPiZK9yBv-waqiicNA32pPL11-HwyNtsGtdK5rQUkHnBlACW4ScROW6Hz6HhaN3rFEwAnCXUHuMpxI_1yh_cDiBWKenAoA0pgwnBaNszbU2jwVihTWGW20g2v2w';

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const createAvatar = async () => {
    setStatus('Creating Avatar...');
    try {
      const response = await axios.post('https://api.meshcapade.com/api/v1/avatars/create/from-images', {}, {
        headers: { 'Authorization': `Bearer ${apiToken}` },
      });
      const newAssetID = response.data.id; // Update according to actual API response structure
      setAssetID(newAssetID);
      setStatus('Avatar Created');
      return newAssetID;
    } catch (error) {
      console.error('Error creating avatar:', error);
      setStatus('Error Creating Avatar');
    }
  };

  const startFittingProcess = async (assetID) => {
    setStatus('Starting Fitting Process...');
    try {
      await axios.post(`https://api.meshcapade.com/api/v1/avatars/${assetID}/fit-to-images`, {}, {
        headers: { 'Authorization': `Bearer ${apiToken}` },
      });
      setStatus('Fitting Process Started');
    } catch (error) {
      console.error('Error starting fitting process:', error);
      setStatus('Error Starting Fitting Process');
    }
  };

  const exportAvatarMesh = async (avatarID) => {
    setStatus('Exporting Avatar Mesh...');
    try {
      const response = await axios.post(`https://api.meshcapade.com/api/v1/avatars/${avatarID}/export`, {}, {
        headers: { 'Authorization': `Bearer ${apiToken}` },
      });
      const meshID = response.data.meshID; // Assuming this is how you get the ID; adjust based on actual API response
      checkExportStatus(meshID);
    } catch (error) {
      console.error('Error exporting avatar mesh:', error);
      setStatus('Error Exporting Avatar Mesh');
    }
  };

  const checkExportStatus = async (exportJobID) => {
    setStatus('Checking Export Status...');
    try {
      let isExportReady = false;
      while (!isExportReady) {
        // Adjust the URL path according to actual API documentation for checking export status
        const statusResponse = await axios.get(`https://api.meshcapade.com/api/v1/exports/${exportJobID}/status`, {
          headers: { 'Authorization': `Bearer ${apiToken}` },
        });

        if (statusResponse.data.status === 'READY') {
          isExportReady = true;
          // Assuming the API returns a URL or some identifier to download the exported mesh
          setModelUrl(statusResponse.data.url); // This might need to be adjusted based on the actual response structure
          setStatus('Export Ready - Mesh can be downloaded');
        } else {
          // Wait for a bit before checking again
          await new Promise(resolve => setTimeout(resolve, 5000)); // Check every 5 seconds
        }
      }
    } catch (error) {
      console.error('Error checking export status:', error);
      setStatus('Error Checking Export Status');
    }
  };


  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!file) {
      alert('Please select a file.');
      return;
    }

    const avatarID = await createAvatar();
    if (avatarID) {
      await startFittingProcess(avatarID);
      await exportAvatarMesh(avatarID); // This function needs to be implemented to handle export and polling
    }
  };

  const Model = () => {
    const obj = useLoader(OBJLoader, modelUrl);
    return <primitive object={obj} />;
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} />
        <button type="submit">Upload and Create Avatar</button>
      </form>
      <div>Status: {status}</div>
      {modelUrl && (
        <Canvas>
          <ambientLight intensity={0.5} />
          <Model />
        </Canvas>
      )}
    </div>
  );
}

export default App;
