import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import * as THREE from 'three';
import { OBJLoader } from 'three/examples/jsm/loaders/OBJLoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

function AvatarCreator() {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('Idle');
  const [avatarId, setAvatarId] = useState('');
  const [exportUrl, setExportUrl] = useState('');
  const apiToken = 'eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJuSnVpYzVwbXk1T1hGSjVmY1RIQTdUNVktRHZVbVVOR2xxVHBqS0hDVnU4In0..iD651MrA49XCFar_hRrr3Wea9SOJKQ9S_voJ3sRAPt5zMMzyvI-tSVP6mBD4UPD4Rzmi4QOqzNz5KamJX-0kXfyF7VM6jSERPfyDwhBY8SeKJTihcyIkOzcPn--ymrtnJymZYgp9bGqduaRab9-z_Y9kwsrdyEeAzta8EOAFusP91dK1VHZPmoJYk9tBP3ckZxIAI_fpZWopgvZR5jCnRbj1YuuVD5yyFZGS4f3wGstrh_bt6KcMwNpbWIfGmMBDWaoOYIgsh2PW0UtnrUvJqINQ4FEVGQR_QM-Yh8m5ls38pQMB3DiF78cFqdSbII4WNzk9w1jU8E3-lLf2XZIsUw';
  const viewerRef = useRef();

  useEffect(() => {
    if (!exportUrl) return;
    initializeViewer(exportUrl);
  }, [exportUrl]);

  const handleFileChange = (event) => setFile(event.target.files[0]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!file) {
      alert("Please select a file.");
      return;
    }

    setStatus('Creating Avatar...');
    try {
      const avatarId = await createAvatar();
      await uploadImage(avatarId);
      await pollForFittingCompletion(avatarId);
      await startExport(avatarId);
    } catch (error) {
      console.error('Error in process:', error);
      setStatus('Error in Process');
    }
  };

  async function createAvatar() {
    const response = await axios.post('https://api.meshcapade.com/api/v1/avatars/create/from-images', {}, {
      headers: { Authorization: `Bearer ${apiToken}` },
    });
    setAvatarId(response.data.data.id);
    return response.data.data.id;
  }

  async function uploadImage(avatarId) {
    const uploadResponse = await axios.post(`https://api.meshcapade.com/api/v1/avatars/${avatarId}/images`, {}, {
      headers: { Authorization: `Bearer ${apiToken}` },
    });
    const uploadUrl = uploadResponse.data.data.links.upload;
    await axios.put(uploadUrl, file, {
      headers: { 'Content-Type': 'image/png' },
    });
    setStatus('Image Uploaded. Starting Fitting Process...');
  }

  async function pollForFittingCompletion(avatarId) {
    let fittingComplete = false;
    while (!fittingComplete) {
      const statusResponse = await axios.get(`https://api.meshcapade.com/api/v1/avatars/${avatarId}`, {
        headers: { Authorization: `Bearer ${apiToken}` },
      });
      if (statusResponse.data.data.attributes.state === 'READY') {
        fittingComplete = true;
        setStatus('Fitting Complete. Starting Export...');
      } else {
        await new Promise(resolve => setTimeout(resolve, 5000)); // Poll every 5 seconds
      }
    }
  }

  async function startExport(avatarId) {
    const exportResponse = await axios.post(`https://api.meshcapade.com/api/v1/avatars/${avatarId}/export`, {
      format: "obj",
      pose: "t",
    }, {
      headers: { Authorization: `Bearer ${apiToken}`, 'Content-Type': 'application/json' },
    });
    const meshId = exportResponse.data.data.id;
    await pollForExportCompletion(meshId);
  }

  async function pollForExportCompletion(meshId) {
    let exportComplete = false;
    while (!exportComplete) {
      const statusResponse = await axios.get(`https://api.meshcapade.com/api/v1/meshes/${meshId}`, {
        headers: { Authorization: `Bearer ${apiToken}` },
      });
      if (statusResponse.data.data.attributes.state === 'READY') {
        exportComplete = true;
        setExportUrl(statusResponse.data.data.attributes.url.path);
        setStatus('Export Complete. Model Ready.');
      } else {
        await new Promise(resolve => setTimeout(resolve, 5000)); // Poll every 5 seconds
      }
    }
  }

  function initializeViewer(exportUrl) {
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, viewerRef.current.clientWidth / viewerRef.current.clientHeight, 0.1, 1000);
    camera.position.z = 5;

    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(viewerRef.current.clientWidth, viewerRef.current.clientHeight);
    viewerRef.current.innerHTML = ''; // Clear the container
    viewerRef.current.appendChild(renderer.domElement);

    const controls = new OrbitControls(camera, renderer.domElement);
    controls.addEventListener('change', () => renderer.render(scene, camera));

    const objLoader = new OBJLoader();
    objLoader.load(exportUrl, (object) => {
      scene.add(object);
      animate();
    });

    function animate() {
      requestAnimationFrame(animate);
      renderer.render(scene, camera);
    }
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} />
        <button type="submit">Create Avatar</button>
      </form>
      <div>Status: {status}</div>
      <div ref={viewerRef} style={{ width: '600px', height: '400px' }}></div>
    </div>
  );
}

export default AvatarCreator;
