import React, { useState, useEffect, useRef } from 'react';
import * as THREE from 'three';
import { OBJLoader } from 'three/examples/jsm/loaders/OBJLoader.js';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls.js';

const OBJViewerTest = () => {
  const [objFile, setObjFile] = useState(null);
  const [textureFile, setTextureFile] = useState(null);
  const viewerRef = useRef(null);

  useEffect(() => {
    if (objFile && textureFile) {
      initThreeScene();
    }
  }, [objFile, textureFile]);

  const handleObjFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setObjFile(file);
    }
  };

  const handleTextureFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setTextureFile(file);
    }
  };

  const initThreeScene = () => {
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x202020); // Set the background color to something dark

    const width = viewerRef.current.clientWidth;
    const height = viewerRef.current.clientHeight;
    const camera = new THREE.PerspectiveCamera(50, width / height, 0.1, 1000);
    camera.position.z = 5;

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.outputEncoding = THREE.sRGBEncoding; // Ensure the renderer uses sRGB color space
    renderer.setSize(width, height);
    viewerRef.current.appendChild(renderer.domElement);

    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;

    // Enhanced three-point lighting setup
    const keyLight = new THREE.DirectionalLight(0xffffff, 1.5); // Brighter key light
    keyLight.position.set(-100, 0, 100);

    const fillLight = new THREE.DirectionalLight(0xffffff, 1.0); // Fill light to reduce shadows
    fillLight.position.set(100, 0, 100);

    const backLight = new THREE.DirectionalLight(0xffffff, 1.0); // Back light for rim effect
    backLight.position.set(100, 0, -100).normalize();

    // Hemisphere light for overall scene illumination and color vibrancy
    const hemiLight = new THREE.HemisphereLight(0xffffff, 0xffffff, 0.6);
    hemiLight.color.setHSL(0.6, 1, 0.6);
    hemiLight.groundColor.setHSL(0.095, 1, 0.75);
    hemiLight.position.set(0, 50, 0);

    scene.add(keyLight, fillLight, backLight, hemiLight);

    const objLoader = new OBJLoader();
    const textureLoader = new THREE.TextureLoader();

    // Read the OBJ file
    const objReader = new FileReader();
    objReader.readAsDataURL(objFile);
    objReader.onload = (event) => {
      objLoader.load(event.target.result, (obj) => {
        // Read the texture file
        const textureReader = new FileReader();
        textureReader.readAsDataURL(textureFile);
        textureReader.onload = (e) => {
          const texture = textureLoader.load(e.target.result);
          obj.traverse((child) => {
            if (child instanceof THREE.Mesh) {
              child.material.map = texture;
              child.material.needsUpdate = true;

              // Adjust material properties to avoid color fading
              child.material.color.convertSRGBToLinear();
              child.material.emissiveIntensity = 0.2;
              child.material.emissive = new THREE.Color('grey').convertSRGBToLinear();
            }
          });
          scene.add(obj);
        };
      });
    };

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    };

    animate();
  };

  return (
    <div>
      <input type="file" onChange={handleObjFileChange} accept=".obj" />
      <input type="file" onChange={handleTextureFileChange} accept="image/*" />
      <div ref={viewerRef} style={{ width: '100vw', height: '100vh' }}></div>
    </div>
  );
};

export default OBJViewerTest;
