import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { OBJLoader } from 'three/examples/jsm/loaders/OBJLoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

function ObjViewerApp() {
  const mountRef = useRef(null);

  useEffect(() => {
    // Scene
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x8FBCD4);

    // Camera
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.z = 5;

    // Renderer
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    mountRef.current.appendChild(renderer.domElement);

    // Lights
    const light = new THREE.HemisphereLight(0xffffbb, 0x080820, 1);
    scene.add(light);

    // Controls
    const controls = new OrbitControls(camera, renderer.domElement);

    function animate() {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    }
    animate();

    // Resizing
    function onWindowResize() {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    }
    window.addEventListener('resize', onWindowResize, false);

    // Cleanup
    return () => {
      window.removeEventListener('resize', onWindowResize);
      mountRef.current.removeChild(renderer.domElement);
    };
  }, []);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const objLoader = new OBJLoader();
        objLoader.parse(e.target.result, (obj) => {
          // Adjustments for 'scene', 'camera', and 'controls' must happen here or within a function called here
          obj.position.set(0, 0, 0);
          obj.scale.set(1, 1, 1);
          scene.add(obj); // 'scene' is accessible here
          // Adjust camera and controls based on the loaded model
        }, undefined, error => console.error(error));
      };
      reader.readAsDataURL(file); // Reads file as a data URL required by OBJLoader
    }
  };

  return (
    <div>
      <input type="file" onChange={handleFileChange} accept=".obj" />
      <div ref={mountRef} style={{ width: '100vw', height: '100vh' }}></div>
    </div>
  );
}

export default ObjViewerApp;
