import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('Idle');
  const [assetID, setAssetID] = useState('');
  const apiToken = 'eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJuSnVpYzVwbXk1T1hGSjVmY1RIQTdUNVktRHZVbVVOR2xxVHBqS0hDVnU4In0..L44V7HsvzlpuToI_5dkZJXf55dHBbtrrDHLbaoPmtVu2lR6SefN1YOup1go0BvzGJ414A8qCjgQVabQ9LPxBUpXKiVsoAKbpW6D1KEPESzsNriM6RRHj7qWaiLflkEjZIa8UZtof86eWU0Fc0SPZbAXV4HlcZLzZ9tlZ4kcBu2FrtlJg7jKwoHga0G7dVg5szgXIUHsVgKCknhRxHc-JEkfjz4JLpg48gwjJpBB1lSLUadcJYjckX-2DLAB7uULR0rnqjFAHHKe1CbIj_0Xyau1FVdtFhNcXiB9HgnKFQ6oI1pF0ra8p_pO7-ZeSgG36MN9DxYnwTKYdabWcV6trGg';
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const createAvatar = async () => {
    setStatus('Creating Avatar...');
    try {
      const response = await axios.post('https://api.meshcapade.com/api/v1/avatars/create/from-images', {}, {
        headers: { 'Authorization': `Bearer ${apiToken}` },
      });
      const newAssetID = response.data.data.id; // Adjust according to actual API response structure
      setAssetID(newAssetID);
      setStatus('Avatar Created');
      return newAssetID;
    } catch (error) {
      console.error('Error creating avatar:', error);
      setStatus('Error Creating Avatar');
      throw new Error('Failed to create avatar');
    }
  };

  const uploadImage = async (assetID) => {
    setStatus('Uploading Image...');
    const formData = new FormData();
    formData.append('file', file);

    try {
      await axios.post(`https://api.meshcapade.com/api/v1/avatars/${assetID}/images`, formData, {
        headers: {
          'Authorization': `Bearer ${apiToken}`,
          'Content-Type': 'multipart/form-data',
        },
      });
      setStatus('Image Uploaded');
    } catch (error) {
      console.error('Error uploading image:', error);
      setStatus('Error Uploading Image');
      throw new Error('Failed to upload image');
    }
  };

  const startFittingProcess = async (assetID) => {
    setStatus('Starting Fitting Process...');
    try {
      await axios.post(`https://api.meshcapade.com/api/v1/avatars/${assetID}/fit-to-images`, {}, {
        headers: { 
          'Authorization': `Bearer ${apiToken}`,
          'Content-Type': 'application/json' // Ensure this header is set if required by the API
        },
      });
      setStatus('Fitting Process Started');
    } catch (error) {
      console.error('Error starting fitting process:', error.response || error);
      setStatus('Error Starting Fitting Process');
      throw new Error(`Failed to start fitting process: ${error.response ? error.response.data : 'Unknown error'}`);
    }
  };



  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!file) {
      alert('Please select a file.');
      return;
    }

    try {
      const createdAssetID = await createAvatar();
      await uploadImage(createdAssetID);
      await startFittingProcess(createdAssetID);
      // Implement a mechanism here to check the status of the fitting process
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} />
        <button type="submit">Upload and Create Avatar</button>
      </form>
      <div>Status: {status}</div>
      {assetID && <div>Asset ID: {assetID}</div>}
    </div>
  );
}

export default App;
