import React, { useRef, useEffect } from 'react';
import * as THREE from 'three';
import { OBJLoader } from 'three/examples/jsm/loaders/OBJLoader';

const ObjViewer = () => {
  const mountRef = useRef(null);

  useEffect(() => {
    const currentMount = mountRef.current;

    // Scene setup
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xdddddd);
    const camera = new THREE.PerspectiveCamera(75, currentMount.clientWidth / currentMount.clientHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(currentMount.clientWidth, currentMount.clientHeight);
    currentMount.appendChild(renderer.domElement);

    // Lighting
    const ambientLight = new THREE.AmbientLight(0x404040); // soft white light
    scene.add(ambientLight);
    const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
    directionalLight.position.set(0, 1, 1);
    scene.add(directionalLight);

    // Camera position
    camera.position.z = 5;

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);
      renderer.render(scene, camera);
    };
    animate();

    // Cleanup
    return () => {
      currentMount.removeChild(renderer.domElement);
    };
  }, []);

  const handleUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => {
        const loader = new OBJLoader();
        loader.load(reader.result, (obj) => {
          // Adjust the object and add it to the scene
          obj.scale.set(0.1, 0.1, 0.1); // Scale the object to a visible size
          scene.add(obj);
          // Fit camera to object
          const box = new THREE.Box3().setFromObject(obj);
          const center = box.getCenter(new THREE.Vector3());
          const size = box.getSize(new THREE.Vector3());
          const maxDim = Math.max(size.x, size.y, size.z);
          const fov = camera.fov * (Math.PI / 180);
          let cameraZ = Math.abs(maxDim / 4 * Math.tan(fov * 2));
          cameraZ *= 2; // a little bit of padding
          camera.position.z = cameraZ;
          const minZ = box.min.z;
          const cameraToFarEdge = (minZ < 0) ? -minZ + cameraZ : cameraZ - minZ;
          camera.far = cameraToFarEdge * 3;
          camera.updateProjectionMatrix();

          // Orbit controls could be added here for better interaction (optional)
        }, undefined, error => console.error('An error occurred:', error));
      };
      reader.readAsDataURL(file); // This line should be removed, it's duplicated
    }
  };

  return (
    <>
      <input type="file" onChange={handleUpload} accept=".obj" />
      <div ref={mountRef} style={{ width: '800px', height: '600px' }}></div>
    </>
  );
};

export default ObjViewer;
