import React, { useState } from 'react';
import axios from 'axios';


function App() {
  const [image, setImage] = useState(null);
  const [assetID, setAssetID] = useState('');
  const [apiToken, setApiToken] = useState('eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJuSnVpYzVwbXk1T1hGSjVmY1RIQTdUNVktRHZVbVVOR2xxVHBqS0hDVnU4In0..kBvnHgNaV-NAQgpUS7N97A-Q0UoPe7yu6tq-QbO4LVTGeI7Urkp2NllBEmV950EzJBHtz5Oe9gVd6LLitOciP1n_YmEbiuRT7uVhbIU8bGL5PKinpcAH1kMwDsc6yxDm8lVWN1Wx8OE_v8ydgR29tzGfKBPDOCavBW0xiLuMKb1DX0BLsQWmrkSpqANjkufnXq5gPRW36lWSytEw_1new8NAEOYrPiZK9yBv-waqiicNA32pPL11-HwyNtsGtdK5rQUkHnBlACW4ScROW6Hz6HhaN3rFEwAnCXUHuMpxI_1yh_cDiBWKenAoA0pgwnBaNszbU2jwVihTWGW20g2v2w');

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  const createAvatar = async () => {
    try {
      // Step 1: Initiate avatar creation from images to get an assetID
      const initResponse = await axios.post('https://api.meshcapade.com/api/v1/avatars/create/from-images', {}, {
        headers: {
          'Authorization': `Bearer ${apiToken}`
        }
      });
      const assetId = initResponse.data.assetID;
      setAssetID(assetId);

      // Step 2: Request image upload URL
      const uploadUrlResponse = await axios.post(`https://api.meshcapade.com/api/v1/avatars/${assetId}/images`, {}, {
        headers: {
          'Authorization': `Bearer ${apiToken}`
        }
      });
      const uploadUrl = uploadUrlResponse.data.uploadUrl;

      // Step 3: Upload the image to the provided URL
      await axios.put(uploadUrl, image, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      // Step 4: Start the fitting process
      await axios.post(`https://api.meshcapade.com/api/v1/avatars/${assetId}/fit-to-images`, {}, {
        headers: {
          'Authorization': `Bearer ${apiToken}`
        }
      });

      alert('Avatar creation initiated successfully.');
    } catch (error) {
      console.error('Error creating avatar:', error);
      alert('Failed to create avatar.');
    }
  };

  return (
    <div>
      <input type="file" onChange={handleImageChange} />
      <button onClick={createAvatar}>Create Avatar</button>
    </div>
  );
};

export default App;
