import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import * as THREE from 'three';
import { OBJLoader } from 'three/examples/jsm/loaders/OBJLoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

function AvatarCreator() {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('Idle');
  const [avatarId, setAvatarId] = useState('');
  const [exportUrl, setExportUrl] = useState('');
  const apiToken = 'eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJuSnVpYzVwbXk1T1hGSjVmY1RIQTdUNVktRHZVbVVOR2xxVHBqS0hDVnU4In0..iD651MrA49XCFar_hRrr3Wea9SOJKQ9S_voJ3sRAPt5zMMzyvI-tSVP6mBD4UPD4Rzmi4QOqzNz5KamJX-0kXfyF7VM6jSERPfyDwhBY8SeKJTihcyIkOzcPn--ymrtnJymZYgp9bGqduaRab9-z_Y9kwsrdyEeAzta8EOAFusP91dK1VHZPmoJYk9tBP3ckZxIAI_fpZWopgvZR5jCnRbj1YuuVD5yyFZGS4f3wGstrh_bt6KcMwNpbWIfGmMBDWaoOYIgsh2PW0UtnrUvJqINQ4FEVGQR_QM-Yh8m5ls38pQMB3DiF78cFqdSbII4WNzk9w1jU8E3-lLf2XZIsUw';
  const viewerRef = useRef();

  useEffect(() => {
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.z = 5;
    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    viewerRef.current.appendChild(renderer.domElement);

    const animate = () => {
      requestAnimationFrame(animate);
      renderer.render(scene, camera);
    };

    if (exportUrl) {
      new OBJLoader().load(exportUrl, (obj) => {
        scene.add(obj);
        animate();
      });
    }

    return () => viewerRef.current && viewerRef.current.removeChild(renderer.domElement);
  }, [exportUrl]);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const createAvatar = async () => {
    setStatus('Creating Avatar...');
    try {
      const response = await axios.post('https://api.meshcapade.com/api/v1/avatars/create/from-images', {}, {
        headers: { Authorization: `Bearer ${apiToken}` },
      });
      const id = response.data.data.id;
      setAvatarId(id);
      setStatus('Avatar Created');
      return id;
    } catch (error) {
      console.error('Error creating avatar:', error);
      setStatus('Error Creating Avatar');
    }
  };

  const uploadImage = async (avatarId) => {
    try {
      const uploadResponse = await axios.post(`https://api.meshcapade.com/api/v1/avatars/${avatarId}/images`, {}, {
        headers: { Authorization: `Bearer ${apiToken}` },
      });
      const uploadUrl = uploadResponse.data.data.links.upload;
      await axios.put(uploadUrl, file, {
        headers: { 'Content-Type': file.type },
      });
      setStatus('Image Uploaded');
    } catch (error) {
      console.error('Error uploading image:', error);
      setStatus('Error Uploading Image');
    }
  };

  const startExportProcess = async (avatarId) => {
    try {
      const response = await axios.post(`https://api.meshcapade.com/api/v1/avatars/${avatarId}/export`, {
        format: "obj",
        pose: "t",
      }, {
        headers: { Authorization: `Bearer ${apiToken}` },
      });
      const meshId = response.data.data.id;
      pollExportStatus(meshId);
    } catch (error) {
      console.error('Error starting export process:', error);
      setStatus('Error Starting Export Process');
    }
  };

  const pollExportStatus = async (meshId) => {
    try {
      const response = await axios.get(`https://api.meshcapade.com/api/v1/meshes/${meshId}`, {
        headers: { Authorization: `Bearer ${apiToken}` },
      });
      const { state, url } = response.data.data.attributes;
      if (state === 'READY') {
        setExportUrl(url.path);
        setStatus('Export Ready');
      } else {
        setTimeout(() => pollExportStatus(meshId), 5000);
      }
    } catch (error) {
      console.error('Error polling export status:', error);
      setStatus('Error Polling Export Status');
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!file) {
      alert('Please select a file.');
      return;
    }

    try {
      setStatus('Processing...');
      const id = await createAvatar();
      await uploadImage(id);
      // Assume startFittingProcess and its polling are implemented here
      // Directly proceeding to export for simplicity
      await startExportProcess(id);
    } catch (error) {
      console.error('Error in the process:', error);
      setStatus('Error in Process');
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} />
        <button type="submit">Create Avatar</button>
      </form>
      <div>Status: {status}</div>
      <div ref={viewerRef} style={{ width: '100%', height: '500px' }} />
    </div>
  );
}

export default AvatarCreator;
