import React, { useRef, useEffect } from 'react';
import * as THREE from 'three';
import { OBJLoader } from 'three/examples/jsm/loaders/OBJLoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

const ObjViewer = () => {
  const mountRef = useRef(null);
  let scene;
  let camera;
  let controls;

  useEffect(() => {
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setPixelRatio(window.devicePixelRatio); // Enhance rendering quality
    mountRef.current.appendChild(renderer.domElement);

    scene.background = new THREE.Color(0xdddddd);

    const ambientLight = new THREE.AmbientLight(0x404040);
    scene.add(ambientLight);
    const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
    directionalLight.position.set(0, 1, 1);
    scene.add(directionalLight);

    camera.position.z = 5;

    // OrbitControls setup
    controls = new OrbitControls(camera, renderer.domElement);
    controls.addEventListener('change', () => renderer.render(scene, camera)); // Use if there is no animation loop
    controls.enableDamping = true; // Optional, but can provide a smoother control feel
    controls.dampingFactor = 0.25;
    controls.enableZoom = true;

    // Removed the continuous animation loop, as OrbitControls will now trigger rendering

    const handleResize = () => {
      renderer.setSize(window.innerWidth, window.innerHeight);
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
    };
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      mountRef.current.removeChild(renderer.domElement);
    };
  }, []);

  const handleUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const objData = e.target.result;
        const loader = new OBJLoader();
        loader.load(objData, (obj) => {
          obj.scale.set(0.1, 0.1, 0.1);
          scene.add(obj);
          controls.target = new THREE.Box3().setFromObject(obj).getCenter(new THREE.Vector3()); // Center controls on the object
          camera.position.z = controls.target.z + 5; // Adjust this value to zoom in more or less
          renderer.render(scene, camera); // Render once after adding the object
        }, undefined, (error) => {
          console.error('An error happened during the loading of the OBJ file:', error);
        });
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <>
      <input type="file" onChange={handleUpload} accept=".obj" />
      <div ref={mountRef} style={{ width: '100vw', height: '100vh' }}></div>
    </>
  );
};

export default ObjViewer;
